SET SERVEROUTPUT ON
SET LINES       190 
SET PAGES       300 
set verify off;
PROMPT 
PROMPT ======================================================
PROMPT  Informe o OWNER do DB Link que deseja descriptografar:
PROMPT ======================================================
ACCEPT p_link_owner  PROMPT '>> '
PROMPT 
PROMPT ======================================================
PROMPT  Informe o nome do DB Link que deseja descriptografar:
PROMPT ======================================================
ACCEPT p_link_name PROMPT '>> '
PROMPT
/*
*  Script baseado no original de:
*     Author : Hatem Mahmoud
*     BLOG   : https://mahmoudhatem.wordpress.com
*     SCRIPT : https://github.com/hatem-mahmoud/scripts/blob/master/db_link_password_decrypt.sql
*
*  Contributor original:
*     Adric Norris (landstander668) : Strip decrypted password to plaintext portion
*     Adric Norris (landstander668) : Allow script to be run using SQL*Plus
*
*  Adaptação:
*     DBA Sobrinho - Ajustado para receber apenas o nome do DBLink como parâmetro,
*     buscar automaticamente os valores de NO_USERID_VERIFIER_SALT (sys.props$)
*     e PASSWORDX (sys.link$), com validação de existência e formatação de saída aprimorada.
*
*  Propósito:
*     Descriptografar a senha de um DBLink
*
*  Testado em:
*     Oracle 12.2.0.1.0 / 19.28.0.0.0
*
*/   
DECLARE
   ztcshpl_v6              VARCHAR2 (32767);
   ztcshpl_v6_offset       NUMBER;
   password_link_offset    NUMBER;
   encryption_key_part_1   VARCHAR2 (64);
   encryption_key_part_2   VARCHAR2 (64);
   encryption_key          VARCHAR2 (128);
   Ciphertext              VARCHAR2 (64);
   initialization_vector   VARCHAR2 (32);
   decrypted_raw           RAW (1000);
   output_string           VARCHAR2 (2000);
   encryption_type         PLS_INTEGER := DBMS_CRYPTO.ENCRYPT_AES256 + DBMS_CRYPTO.CHAIN_CBC + DBMS_CRYPTO.PAD_NONE;
 --Inicio Adapatacao DBASobrinho  
   NO_USERID_VERIFIER_SALT sys.props$.value$%TYPE;
   password_link           sys.link$.passwordx%TYPE;
   p_link_name             VARCHAR2(200) := '&p_link_name';
   p_link_owner             VARCHAR2(200) := '&p_link_owner';
--Fim Adapatacao DBASobrinho
BEGIN
--Inicio Adapatacao DBASobrinho
	   BEGIN
		  SELECT value$
			INTO NO_USERID_VERIFIER_SALT
			FROM sys.props$
		   WHERE name = 'NO_USERID_VERIFIER_SALT'
		   and value$ is not null;
	   EXCEPTION
		  WHEN NO_DATA_FOUND THEN
			 DBMS_OUTPUT.put_line('Erro: NO_USERID_VERIFIER_SALT não encontrado em sys.props$');
			 RETURN;
	   END;

	   BEGIN
			SELECT l.passwordx
			  INTO password_link
			  FROM sys.link$ l
			  JOIN sys.user$ u ON l.owner# = u.user#
			 WHERE u.name = UPPER(p_link_owner)
			   AND l.name = UPPER(p_link_name)
			   AND l.passwordx IS NOT NULL;
	   EXCEPTION
		  WHEN NO_DATA_FOUND THEN
			 DBMS_OUTPUT.put_line('Erro: DBLINK "' || UPPER(p_link_name) || '" não encontrado em sys.link$');
			 RETURN;
	   END;
--Fim Adapatacao DBASobrinho


   password_link_offset := 3;
   ztcshpl_v6 :=
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '' ||
         '';

   encryption_key_part_2 :=
      DBMS_CRYPTO.hash (src   => NO_USERID_VERIFIER_SALT,
                        typ   => DBMS_CRYPTO.HASH_SH256);

   FOR i IN 0 .. 63
   LOOP
      --Logical shift  shl in assembly
      ztcshpl_v6_offset :=
             (TO_NUMBER (SUBSTR (password_link, 3, 2), 'xx') * POWER (2, 6))* 2+ 1+ i * 2;

      IF (initialization_vector IS NULL)
      THEN
         initialization_vector := SUBSTR (ztcshpl_v6, ztcshpl_v6_offset, 32);
      END IF;

      password_link_offset := password_link_offset + TO_NUMBER (SUBSTR (ztcshpl_v6, ztcshpl_v6_offset, 2), 'xx') * 2 + 2;

      IF (i < 32)
      THEN
         encryption_key_part_1 := encryption_key_part_1 || SUBSTR (password_link, password_link_offset, 2);
      ELSE
         Ciphertext := Ciphertext || SUBSTR (password_link, password_link_offset, 2);
      END IF;
   END LOOP;


   encryption_key :=utl_raw.bit_xor(encryption_key_part_1,encryption_key_part_2);


   decrypted_raw :=
      DBMS_CRYPTO.DECRYPT (src   => Ciphertext,
                           typ   => encryption_type,
                           key   => encryption_key,
                           iv    => initialization_vector);
      output_string := UTL_I18N.RAW_TO_CHAR ( utl_raw.substr( decrypted_raw, 2,to_number( rawtohex( utl_raw.substr( decrypted_raw, 1, 1 ) ), 'XX' )));



   DBMS_OUTPUT.put_line ('----------DECRYPTING DB LINK PASSWORD--------------');
   DBMS_OUTPUT.put_line ('Initialization_vector            : '||initialization_vector);
   DBMS_OUTPUT.put_line ('---------------------------------------------------');
   DBMS_OUTPUT.put_line ('Ciphertext                       : '||Ciphertext);
   DBMS_OUTPUT.put_line ('---------------------------------------------------');
   DBMS_OUTPUT.put_line ('Encryption key part 1            : '||encryption_key_part_1);
   DBMS_OUTPUT.put_line ('---------------------------------------------------');
   DBMS_OUTPUT.put_line ('Encryption key part 2            : '||encryption_key_part_2);
   DBMS_OUTPUT.put_line ('---------------------------------------------------');
   DBMS_OUTPUT.put_line ('Encryption key (Part 1 / Part 2) : '||encryption_key);
   DBMS_OUTPUT.put_line ('---------------------------------------------------');
   DBMS_OUTPUT.put_line ('Hex (opcional)                   : ' || decrypted_raw);
   DBMS_OUTPUT.put_line ('---------------------------------------------------');
   DBMS_OUTPUT.put_line ('');
   DBMS_OUTPUT.put_line ('!! Senha do DBLink descriptografada !!');
   DBMS_OUTPUT.put_line ('');
   DBMS_OUTPUT.put_line ('====================[ GUINA NAO TINHA DO ]=====================');
   DBMS_OUTPUT.put_line ('');
   DBMS_OUTPUT.put_line ('          >>        '|| output_string ||'        <<         ');
   DBMS_OUTPUT.put_line ('');
   DBMS_OUTPUT.put_line ('====================[ GUINA NAO TINHA DO ]=====================');
   DBMS_OUTPUT.put_line ('');
   DBMS_OUTPUT.put_line ('. . . ');
END;
/